using System.Collections.Generic;using UnityEngine;using System.IO;using Newtonsoft.Json;public class Skill  {		public int Skill_id { get; set; }		public string Skill_Name { get; set; }		public  List<float> Skill_S2 { get; set; }		public  List<float> Skill_S3 { get; set; }		public  List<float> Skill_S4 { get; set; }		 		public static string configName = "Skill";		public static Skill config { get; set; }		public string version { get; set; }		public List<Skill> datas { get; set; }		public static void Init()		{			string folderPath = "D:/Unity code And project/JsonTeach/JsonDotNet_Teach/JsonNetTeachPro/Assets/Json/";			string[] filePaths = Directory.GetFiles(folderPath, configName + ".json");			if (filePaths != null)
			{
				string jsonContent = File.ReadAllText(filePaths[0]);
				config = JsonConvert.DeserializeObject<Skill>(jsonContent);			 }		}		public static Skill Get(int Skill_id)		{			Init();			foreach (var item in config.datas)			{				if (item.Skill_id == Skill_id)				{ 					return item;				}			}			return null;		}}