using System.Collections.Generic;using UnityEngine;using System.IO;using Newtonsoft.Json;public class item  {		public int id { get; set; }		public string Name { get; set; }		public string res { get; set; }		public string icon_Name { get; set; }		public int Type_Name { get; set; }		 		public static string configName = "item";		public static item config { get; set; }		public string version { get; set; }		public List<item> datas { get; set; }		public static void Init()		{			string folderPath = "D:/Unity code And project/JsonTeach/JsonDotNet_Teach/JsonNetTeachPro/Assets/Json/";			string[] filePaths = Directory.GetFiles(folderPath, configName + ".json");			if (filePaths != null)
			{
				string jsonContent = File.ReadAllText(filePaths[0]);
				config = JsonConvert.DeserializeObject<item>(jsonContent);			 }		}		public static item Get(int id)		{			Init();			foreach (var item in config.datas)			{				if (item.id == id)				{ 					return item;				}			}			return null;		}}